ST.xhtml:
(* Control from the graphics *)
IF NOT xgv02.Remote THEN
	xgv02.HorizontalLeft := xgv02.HMIDownLeft OR xgv02.HMILeft OR xgv02.HMIUpLeft;
	xgv02.HorizontalRight := xgv02.HMIDownRight OR xgv02.HMIRight OR xgv02.HMIUpRight;
	xgv02.VerticalUp := xgv02.HMIUpLeft OR xgv02.HMIUp OR xgv02.HMIUpRight;
	xgv02.VerticalDown := xgv02.HMIDownLeft OR xgv02.HMIDown OR xgv02.HMIDownRight;
	xgv02.OpenWaterValve := xgv02.HMIOpenWater;
END_IF;

(* Coordinate control *)
IF xgv02.Remote AND vManual THEN
	vXCMD := xgv02.HMICoorXCmd;
	vYCMD := xgv02.HMICoorYCmd;
ELSE
	xgv02.HMICoorXCmd := xgv02.PositionX;
	xgv02.HMICoorYCmd := xgv02.PositionY;
END_IF

(* Full automatic algorithm *)
IF xgv02.Remote AND NOT vManual THEN
	vXCMD := vFlowersXY[i,0]+ 30;
	vYCMD := vFlowersXY[i,1]+ 30;

	ActuatorOverFlower(
		xcor1:= xgv02.PositionX, 
		ycor1:= xgv02.PositionY, 
		height1:= 10, 
		width1:= 10, 
		xcor2:= vFlowersXY[i,0], 
		ycor2:= vFlowersXY[i,1], 
		height2:= 70, 
		width2:= 70);

	IF 	ActuatorOverFlower.isCollision THEN
		Watering(IN:=TRUE, PT:=T#3S);
		xgv02.OpenWaterValve := TRUE;
	END_IF;
	IF ActuatorOverFlower.isCollision AND Watering.Q THEN
		Watering(IN := FALSE);
		xgv02.OpenWaterValve:= FALSE;
		i:= i + 1;	
	END_IF
END_IF
IF i = 59 THEN
	i := 0;
END_IF

(* Actual motor movement section *)
IF xgv02.Remote THEN
	IF vXCMD > xgv02.PositionX - 2 THEN
		xgv02.HorizontalLeft := FALSE;
		xgv02.HorizontalRight := TRUE;
	ELSIF vXCMD < xgv02.PositionX + 2 THEN
		xgv02.HorizontalLeft := TRUE;
		xgv02.HorizontalRight := FALSE;
	ELSE
		xgv02.HorizontalLeft := FALSE;
		xgv02.HorizontalRight := FALSE;
	END_IF;

	IF vYCMD < xgv02.PositionY - 2THEN
		xgv02.VerticalUp := TRUE;
		xgv02.VerticalDown := FALSE;
	ELSIF vYCMD > xgv02.PositionY + 2 THEN
		xgv02.VerticalUp := FALSE;
		xgv02.VerticalDown := TRUE;
	ELSE
		xgv02.VerticalUp := FALSE;
		xgv02.VerticalDown := FALSE;
	END_IF;
END_IF;

vStartBlinker := (CmdFwd OR CmdRev) AND NOT (CmdFwd AND CmdRev);
Blink(ENABLE := vStartBlinker, TIMELOW:= PulseWidth, TIMEHIGH := PulseWidth);

tonS2(IN := blink.OUT, PT:=PulseWidth/2);
tofS2(IN := NOT tonS2.Q AND blinkOld, PT:=PulseWidth/2);

blinkOld := tonS2.Q;

IF CmdFwd THEN
	S1 := blink.OUT;
	S2 := tonS2.Q OR tofS2.Q;
ELSIF CmdRev THEN
	S1 := tonS2.Q OR tofS2.Q;
	S2 := blink.OUT;
END_IF

(* The actual servos that move the watering equipment *)
vHorizontalLimit := (vPositionX >= 940 AND NOT xgv02.HorizontalLeft)
				  OR (vPositionX <= 0 AND NOT xgv02.HorizontalRight);
vVerticalLimit := (vPositionY >= 500 AND NOT xgv02.VerticalUp)
			    OR (vPositionY <= 0 AND NOT xgv02.VerticalDown);

(* Maximum range - 940 steps *)
HorizontalAxis(
	CmdFwd:= xgv02.HorizontalRight AND NOT vHorizontalLimit, 
	CmdRev:= xgv02.HorizontalLeft AND NOT vHorizontalLimit, 
	PulseWidth:= T#0.1S , 
	S1=> xgv02.HorizontalS1, 
	S2=> xgv02.HorizontalS2);

(* Maximum range - 500 steps *)	
VerticalAxis(
	CmdFwd:= xgv02.VerticalDown AND NOT vVerticalLimit, 
	CmdRev:= xgv02.VerticalUp AND NOT vVerticalLimit, 
	PulseWidth:= T#0.1S , 
	S1=> xgv02.VerticalS1, 
	S2=> xgv02.VerticalS2);

IF xgv02.HorizontalS1 <> vHorizontalS1Old THEN
	IF xgv02.HorizontalS1 <> xgv02.HorizontalS2 THEN
		rtHIncrement(CLK:=TRUE);
	ELSE
		rtHDecrement(CLK:=TRUE);
	END_IF
ELSE
	rtHIncrement(CLK:=FALSE);
	rtHDecrement(CLK:=FALSE);
END_IF

IF xgv02.VerticalS1 <> vVerticalS1Old THEN
	IF xgv02.VerticalS1 <> xgv02.VerticalS2 THEN
		rtVIncrement(CLK:=TRUE);
	ELSE
		rtVDecrement(CLK:=TRUE);
	END_IF
ELSE
	rtVIncrement(CLK:=FALSE);
	rtVDecrement(CLK:=FALSE);
END_IF

PositionCounterHorizontalAxis(
	CU:= rtHIncrement.Q, 
	CD:= rtHDecrement.Q, 
	CV=> vHorizontalPos);

PositionCounterVerticalAxis(
	CU:= rtVIncrement.Q, 
	CD:= rtVDecrement.Q, 
	CV=> vVerticalPos);

(* Position of the system in the graphics*)
(* For every 1 pulses we move 5 px *)
vPositionX := (DINT_TO_REAL(vHorizontalPos)/ 1) * 5;
vPositionY := (DINT_TO_REAL(vVerticalPos)/ 1) * 5;

vHorizontalS1Old := xgv02.HorizontalS1;
vVerticalS1Old := xgv02.VerticalS1;
(* This is to counter the starting offset in the graphics *)
vPlatformActualPosX := 307 + vPositionX;
vPlatformActualPosY := 279 + vPositionY;

(* Send the platform position to other programs *)
xgv02.PositionX := vPlatformActualPosX;
xgv02.PositionY := vPlatformActualPosY;
