		servoX_CCW => gIO.Garden4_ServoX_CCW,
		servoY_CW => gIO.Garden4_ServoY_CW,
		servoY_CCW => gIO.Garden4_ServoY_CCW,
		waterTankOpenCmd => gIO.Garden4_WaterTankOpenCmd);
END_IF

(* Full automatic algorithm *)
vXAxisCmd := flowerLocations[i,0];
vYAxisCmd := flowerLocations[i,1];

(*Check if the platform is above the flower. We can allow +- 5 increments wrong position*)
vPlatformAtTarget := xAxis = vXAxisCmd AND yAxis = vYAxisCmd;

IF vPlatformAtTarget THEN
	(*Start watering for 1 second*)
	WateringTimer(IN:=TRUE, PT:=T#1S);
	waterTankOpenCmd := TRUE;

	(*If watering is finished, go to next flower*)
	IF WateringTimer.Q THEN
		WateringTimer(IN := FALSE);
		waterTankOpenCmd:= FALSE;
		i:= i + 1;
	END_IF
END_IF

(*If we went over the array, then start at first flower again*)
IF i = 25 THEN
	i := 0;
END_IF

(* Actual motor movement algorithm *)
IF vXAxisCmd > xAxis THEN
	servoX_CCW := FALSE;
	servoX_CW := TRUE;
ELSIF vXAxisCmd < xAxis THEN
	servoX_CCW := TRUE;
	servoX_CW := FALSE;
ELSE
	servoX_CCW := FALSE;
	servoX_CW := FALSE;
END_IF

IF vYAxisCmd < yAxis THEN
	servoY_CCW := TRUE;
	servoY_CW := FALSE;
ELSIF vYAxisCmd > yAxis THEN
	servoY_CCW := FALSE;
	servoY_CW := TRUE;
ELSE
	servoY_CCW := FALSE;
	servoY_CW := FALSE;
END_IF
