edge(CLK:=clock.OUT);
pulse := edge.Q;

gIO.Garden1_ServoX_CW := (gIO.Garden1_ServoX_Offset < 404) AND NOT gIO.Garden1_ServoX_CCW;
gIO.Garden1_ServoX_CCW := (gIO.Garden1_ServoX_Offset > 0) AND NOT gIO.Garden1_ServoX_CW;

gIO.Garden1_ServoY_CW := (gIO.Garden1_ServoY_Offset < 320) AND NOT gIO.Garden1_ServoY_CCW;
gIO.Garden1_ServoY_CCW := (gIO.Garden1_ServoY_Offset > 0) AND NOT gIO.Garden1_ServoY_CW;

gIO.Garden1_WaterTankOpenCmd := gIO.Garden1_ServoY_CW;


